/** @format */

import { useContext, useState } from 'react';
import { useTextAreaStyles } from './styles';
import { TextareaAutosize } from '@material-ui/core';
import LightButton from '../Button/Button';
import { social } from '../Profile/Data';
import { createContext } from 'react';
import { AppTheme } from '../../contexts/AppTheme';

type ThemeContextProps = {
	theme: string;
	setTheme: React.Dispatch<React.SetStateAction<string>>;
	label: string; // Define the type of the label prop
	onChange: (event: React.ChangeEvent<HTMLTextAreaElement>) => void;
	value: string;
};

export const ThemeContext = createContext({} as ThemeContextProps);
const TextArea = ({ label }: ThemeContextProps) => {
	const [value, setValue] = useState('');
	const classes = useTextAreaStyles();

	const handleChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
		setValue(event.target.value); // Update value state when textarea content changes
	};
	const handlChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
		setValue(event.target.value); // Update value state when textarea content changes
	};

	const [theme, setTheme] = useState('light');

	const AppStyle: AppTheme = {
		dark: {
			backgroundColor: '#121212',
			color: 'white',
		},
		light: {
			backgroundColor: 'white',
			color: 'black',
		},
		common: {
			transition: 'all 1s ease',
		},
	};

	const themeStyle = {
		...AppStyle.common,
		...(theme === 'light' ? AppStyle.light : AppStyle.dark),
	};
	return (
		<section className={classes.root}>
			{label && <label>{label}</label>}
			<form className={classes.form}>
				<TextareaAutosize
					className={classes.textArea}
					onChange={handleChange}
					name="Full Name"
				/>
				<TextareaAutosize
					className={classes.textArea}
					onChange={handlChange}
					name="email"
				/>
				<TextareaAutosize
					className={classes.textArea}
					onChange={handleChange}
					style={{ height: '500px' }}
					name="discription"
				/>
				<div className={classes.formFooter}>
					<LightButton text="Send Messsage" />

					<section className={classes.linkContainer}>
						{social.map((item) => {
							const { id, url, icon, text, dash } = item;
							return (
								<div className={classes.socialInfo}>
									<div
										className={classes.social}
										key={id}>
										{' '}
										<a
											href={url}
											target="_blank"
											rel="noopener noreferrer"
											className={classes.url}>
											<p className={classes.infoText}>
												{text}
												{icon} <span> {dash}</span>
											</p>{' '}
										</a>
									</div>
								</div>
							);
						})}
					</section>
				</div>
			</form>
		</section>
	);
};

export default TextArea;
