/** @format */

import * as React from 'react';
import { useAppStyles } from './styles';
import { theme } from './theme';
import { createContext, useState } from 'react';
import About from './components/About/About';
import NavBar from './components/NavBar/NavBar';
import Profile from './components/Profile/Profile';
// import { useThemeContext } from './context/theme';

import Featured from './components/Featured/Featured';
import Contact from './components/Contact/Contact';
import { AppTheme } from './contexts/AppTheme';
import { Theme } from '@mui/material';
import { makeStyles } from '@mui/styles';

type ThemeContextProps = {
	theme: string;
	setTheme: React.Dispatch<React.SetStateAction<string>>;
};
export const ThemeContext = createContext({} as ThemeContextProps);

export const useAboutStyles = makeStyles((theme: Theme) => ({
	root: {
		// display: "flex",
		margin: 'auto',

		justifyContent: 'space-evenly',

		fontFamily: 'Clash display',
	},
	container: {
		display: 'flex',
		justifyContent: 'space-between',
		width: '1440px',
		margin: 'auto',
	},
	content: {
		width: '65%',
		fontSize: '40px',
		marginBottom: '16px',
	},
	btnGroup: { display: 'flex', gap: 20 },
}));

export default function App() {
	const [theme, setTheme] = useState('light');

	const AppStyle: AppTheme = {
		dark: {
			background: theme.design.ayo.waz_black,
			color: theme.design.ayo.waz_white,
		},
		light: {
			backgroundColor: 'white',
			color: 'black',
		},
		common: {
			transition: 'all 1s ease',
		},
	};

	const themeStyle = {
		...AppStyle.common,
		...(theme === 'light' ? AppStyle.light : AppStyle.dark),
	};

	return (
		<ThemeContext.Provider value={{ theme, setTheme }}>
			<div style={themeStyle}>
				<NavBar
					url={''}
					title={''}
					id={0}
				/>
				<Profile />
				<About />
				<Featured />
				<Contact />
			</div>
		</ThemeContext.Provider>
	);
}
