/** @format */

import * as React from 'react';
import { useEffect, useState } from 'react';
import { AppTheme } from './contexts/AppTheme';
import { ThemeContext } from './ThemeContext'; // Assuming ThemeContext is the correct import

const App = () => {
	const [theme, setTheme] = useState(getStorageTheme());

	const AppStyle: AppTheme = {
		dark: {
			backgroundColor: '#121212',
			color: 'white',
		},
		light: {
			backgroundColor: 'white',
			color: 'black',
		},
		common: {
			transition: 'all 1s ease',
		},
	};

	const themeStyle = {
		...AppStyle.common,
		...(theme === 'light' ? AppStyle.light : AppStyle.dark),
	};

	useEffect(() => {
		document.documentElement.className = theme;
		localStorage.setItem('theme', theme);
	}, [theme]);

	function getStorageTheme() {
		let theme = 'light';
		if (localStorage.getItem('theme')) {
			localStorage.getItem('theme');
		}
		return theme;
	}

	return (
		<ThemeContext.Provider value={{ theme, setTheme }}>
			<div style={themeStyle}>{/* Your components here */}</div>
		</ThemeContext.Provider>
	);
};

export default App;
