/** @format */

// eslint-disable-next-line @typescript-eslint/no-unused-vars
import { ThemeContext } from '@emotion/react';
import { Theme } from '@mui/material';
import { makeStyles } from '@mui/styles';

export const useProjectStyles = makeStyles((theme: Theme) => ({
	root: {
		display: 'flex',
		margin: 'auto',
		alignItems: 'center',
		height: theme.spacing(12.5),
		fontFamily: 'Clash Display',
		width: '1440px',
	},
	flexContainer: {
		display: 'flex',
		flexWrap: 'wrap',
		justifyContent: 'space-between',
	},
	flexItem: {
		flex: '0 0 calc(50% - 10px)', // Two columns with a small gap between them
		marginBottom: '20px',
		padding: '10px',
		boxSizing: 'border-box',
		textAlign: 'justify',
		width: '70%',
		'& img': {
			width: '100%',
			height: 'auto',
		},
		'& p': {
			textAlign: 'center',
		},
		'& a': {
			display: 'block',
			textAlign: 'center',
			textDecoration: 'none',
			color: theme.palette.primary.main,
			fontWeight: 'bold',
			marginBottom: '5px',
		},
	},
	image: {
		maxWidth: '100%',
		height: 'auto',
	},
	sectionName: {},
	container: { display: 'flex' },
}));
